mapper.select:
SELECT A.IN_NO, TO_CHAR(A.IN_DATE, 'yyyy-MM-dd') AS IN_DATE, A.MTR_CD, B.MTR_NM, B.UNIT, FN_GET_BAS_DTL_CD_TO_NM(B.UNIT) AS UNIT_NM, A.ORD_NO, A.FLT_AMT, A.IN_AMT, A.TOT_COST, A.UNIT_COST, FN_GET_BAS_DTL_CD_TO_NM(B.COMP_CD) AS COMP_NM FROM MTR_IN A, ADM_MTR B WHERE A.MTR_CD = B.MTR_CD
AND TO_CHAR(A.IN_DATE, 'yyyy-MM-dd') >= #{startDate}
AND TO_CHAR(A.IN_DATE, 'yyyy-MM-dd') <= #{endDate}
AND B.COMP_CD = #{compCd}
AND B.MTR_CD = #{ditemCode}

mapper.update:
update MTR_LOT_STCK
set 
HLD_CNT = #{hldCnt}
WHERE 
MTR_LOT = #{mtrLot}
