mapper.select:
SELECT A.IN_NO, A.MTR_CD, A.ORD_NO, A.FLT_AMT, A.IN_AMT, A.TOT_COST, A.UNIT_COST, B.MTR_NM, B.UNIT, C.ORD_AMT, C.NOTIN_AMT, TO_CHAR(A.IN_DATE, 'yyyy-MM-dd') AS IN_DATE, FN_GET_BAS_DTL_CD_TO_NM(B.UNIT) AS UNIT_NM, FN_GET_BAS_DTL_CD_TO_NM(B.COMP_CD) AS COMP_NM FROM MTR_IN A, ADM_MTR B, MTR_ORD_DTL C WHERE A.MTR_CD = B.MTR_CD AND A.MTR_CD = C.MTR_CD(+) AND A.ORD_NO = C.ORD_NO(+)
AND TO_CHAR(A.IN_DATE, 'yyyy-MM-dd') >= #{startDate}
AND TO_CHAR(A.IN_DATE, 'yyyy-MM-dd') <= #{endDate}
AND B.COMP_CD = #{compCd}
AND B.MTR_CD = #{ditemCode}

mapper.update:
UPDATE MTR_ORD_DTL SET IN_AMT = (NVL(IN_AMT,0)-NVL(#{inAmt},0)),
FLT_AMT = (NVL(FLT_AMT,0)-NVL(#{fltAmt},0)),
NOTIN_AMT = NOTIN_AMT +NVL(#{inAmt},0) +NVL(#{fltAmt},0) WHERE ORD_NO = #{ordNo} AND MTR_CD = #{mtrCd}

update MTR_LOT_STCK
set 
HLD_CNT = hld_cnt+ #{hldCnt}
WHERE 
MTR_LOT = #{mtrLot}
