mapper.select:
SELECT A.ORD_NO, A.MTR_CD, A.FLT_AMT AS RTN_AMT, A.ORD_AMT, A.UNIT_COST, (A.ORD_AMT*A.UNIT_COST) AS TOT_COST, A.IN_AMT, A.NOTIN_AMT, TO_CHAR(B.ORD_DATE, 'yyyy-MM-dd') AS ORD_DATE, FN_GET_BAS_DTL_CD_TO_NM(B.COMP_CD) AS COMP_NM, FN_GET_MTR_CD_TO_NM(A.MTR_CD) AS MTR_NM, FN_GET_BAS_DTL_CD_TO_NM(A.UNIT) AS UNIT FROM MTR_ORD_DTL A, MTR_ORD B WHERE A.ORD_NO = B.ORD_NO AND (A.FLT_AMT/A.ORD_AMT*100) > 5
AND TO_CHAR(B.ORD_DATE, 'yyyy-MM-dd') >= #{startDate}
AND TO_CHAR(B.ORD_DATE, 'yyyy-MM-dd') <= #{endDate}
AND B.COMP_CD = #{compCd}
AND A.MTR_CD = #{ditemCode}
ORDER BY B.ORD_DATE

SELECT A.RTN_NO, TO_CHAR(A.RTN_REQ_DATE, 'yyyy-MM-dd') AS RTN_REQ_DATE, A.MTR_CD, B.MTR_NM, FN_GET_BAS_DTL_CD_TO_NM(B.UNIT) AS UNIT, FN_GET_BAS_DTL_CD_TO_NM(B.COMP_CD) AS COMP_NM, A.RTN_AMT, A.CMT, C.UNIT_COST, C.TOT_COST FROM MTR_RTN A, ADM_MTR B, MTR_ORD_DTL C WHERE A.MTR_CD = B.MTR_CD AND A.ORD_NO = C.ORD_NO AND A.MTR_CD = C.MTR_CD
AND TO_CHAR(A.RTN_REQ_DATE, 'yyyy-MM-dd') >= #{startDate}
AND TO_CHAR(A.RTN_REQ_DATE, 'yyyy-MM-dd') <= #{endDate}
AND B.COMP_CD = #{compCd}
AND B.MTR_CD = #{ditemCode}
