mapper.select:
SELECT a.MTR_LOT, a.MTR_CD, a.IN_NO, a.HLD_CNT, a.STCK_CNT, a.CMT, FN_GET_MTR_CD_TO_NM(a.MTR_CD) AS MTR_NM, FN_GET_BAS_DTL_CD_TO_NM(b.UNIT) AS UNIT_NM, FN_GET_BAS_DTL_CD_TO_NM(b.MTR_SECT) AS MTR_SECT_NM FROM MTR_LOT_STCK a, ADM_MTR b, MTR_IN c WHERE a.MTR_CD = b.MTR_CD AND a.IN_NO = c.IN_NO
AND TO_CHAR(c.IN_DATE, 'yyyy-MM-dd') >= #{startDate}
AND TO_CHAR(c.IN_DATE, 'yyyy-MM-dd') <= #{endDate}
AND a.MTR_CD = #{ditemCode}
AND b.MTR_SECT = #{mtrSect}
ORDER BY MTR_LOT

SELECT a.MTR_CD, SUM(a.HLD_CNT) AS HLD_CNT, SUM(a.STCK_CNT) AS MTR_STCK_CNT, b.SAFE_STCK, b.USE_YN, FN_GET_MTR_CD_TO_NM(a.MTR_CD) AS MTR_NM, FN_GET_BAS_DTL_CD_TO_NM(b.UNIT) AS UNIT_NM, FN_GET_BAS_DTL_CD_TO_NM(b.MTR_SECT) AS MTR_SECT_NM FROM MTR_LOT_STCK a, ADM_MTR b, MTR_IN c WHERE a.MTR_CD = b.MTR_CD AND a.IN_NO = c.IN_NO
AND TO_CHAR(c.IN_DATE, 'yyyy-MM-dd') >= #{startDate}
AND TO_CHAR(c.IN_DATE, 'yyyy-MM-dd') <= #{endDate}
AND a.MTR_CD = #{ditemCode}
AND b.MTR_SECT = #{mtrSect}
GROUP BY a.MTR_CD, b.SAFE_STCK, b.USE_YN, FN_GET_BAS_DTL_CD_TO_NM(a.MTR_CD),FN_GET_BAS_DTL_CD_TO_NM(b.UNIT), FN_GET_BAS_DTL_CD_TO_NM(b.MTR_SECT) ORDER BY MTR_CD
