mapper.select:
select instr_no, line_no , prd_cd , instr_cnt , pdt_cnt , work_str_date , work_str_time , bom_cd from pdt_work_instr_dtl group by instr_no, line_no , prd_cd , instr_cnt , pdt_cnt , work_str_date , work_str_time , bom_cd having 1=1
and instr_no=#{instrNo}
and prd_cd like '%'||#{prdCd}||'%'
and work_str_date>#{startDate}
and #{endDate}>work_str_date

select instr_no, line_no , prd_cd , instr_cnt , pdt_cnt , work_str_date , work_str_time , bom_cd ,prc_cd from pdt_work_instr_dtl group by instr_no, line_no , prd_cd , instr_cnt , pdt_cnt , work_str_date , work_str_time , bom_cd ,prc_cd having 1=1
and work_str_date>#{startDate}
and #{endDate}>work_str_date

mapper.update:
update MTR_LOT_STCK
set
HLD_CNT = hld_cnt-#{hldCnt},
STCK_CNT = STCK_CNT-#{hldCnt}
WHERE
MTR_LOT = #{mtrLot}
