mapper.select:
		select now_phs,work_str_dt,instr_no,nvl(flt_cnt,0) fltCnt,prc_cd,nvl(pdt_cnt,0) pdt_cnt ,nvl((select sum(flt_cnt) 
                                    from prd_prc_mng
                                    where instr_no=#{instrNo}
                                    group by instr_no),0) as sumFlt
		from prd_prc_mng
		where prc_cd=#{prcCd} AND instr_no=#{instrNo}

mapper.update:
update pdt_work_instr set
now_phs='Y'
where instr_no=#{instrNo}

mapper.select:
select * from used_mtr_lot where instr_no like '%PWO%'

	and instr_no=#{instrNo}


	and mtr_lot=#{mtrLot}

select a.instr_no , a.line_no , a.work_str_dt , a.work_fin_dt , a.goal_cnt, a.pdt_cnt from (select distinct a.instr_no , a.line_no , to_char(b.work_str_dt,'yyyy-MM-dd') work_str_dt, to_char(b.work_fin_dt,'yyyy-MM-dd') work_fin_dt, b.goal_cnt , (select min(pdt_cnt) from prd_prc_mng where instr_no=a.instr_no) as pdt_cnt from used_mtr_lot a join prd_prc_mng b on a.instr_no = b.instr_no where 1=1 and a.instr_no like '%PWO%'
and mtr_lot=#{mtrLot}
) a join pdt_work_instr b on a.instr_no = b.instr_no where b.now_phs='Y'
and work_str_dt>#{startDate}
and #{endDate}>work_str_dt

select distinct b.line_no , b.instr_no ,b.work_str_dt , b.goal_cnt , b.now_phs from used_mtr_lot a join (select line_no , instr_no ,work_str_dt , goal_cnt , now_phs from( select distinct b.line_no , a.instr_no , to_char(a.instr_date,'yyyy-MM-dd') work_str_dt , b.instr_cnt goal_cnt, a.now_phs from pdt_work_instr a join pdt_work_instr_dtl b on a.instr_no = b.instr_no where a.now_phs='N')) b on a.instr_no = b.instr_no where 1=1
and b.work_str_dt>#{startDate}
and #{endDate}>b.work_str_dt
and a.mtr_lot=#{mtrLot}
order by b.work_str_dt

select count(instr_no) 
from prd_prc_mng
where instr_no=#{instrNo}

select trunc(((a.uph_pdt_amt)/60/6),3)
from fct_line a join fct_info b
on a.line_no = b.line_no
where a.line_no=#{lineNo}
and b.prc_cd=#{prcCd}
order by a.line_no

select * from prd_prc_mng
where to_char(work_str_dt,'yyyy-MM-dd') = to_char(sysdate , 'yyyy-MM-dd')
and line_no =#{lineNo}
and instr_no !=#{instrNo}
and prc_cd = #{prcCd}
and now_phs = '가동중'
